/*
 * [200] Number of Islands
 *
 * https://leetcode.com/problems/number-of-islands
 *
 * Medium (32.95%)
 * Total Accepted:    91620
 * Total Submissions: 278014
 * Testcase Example:  '["11110","11010","11000","00000"]'
 *
 * Given a 2d grid map of '1's (land) and '0's (water), count the number of
 * islands. An island is surrounded by water and is formed by connecting
 * adjacent lands horizontally or vertically. You may assume all four edges of
 * the grid are all surrounded by water.
 * 
 * Example 1:
 * 11110110101100000000
 * Answer: 1
 * Example 2:
 * 11000110000010000011
 * Answer: 3
 * 
 * Credits:Special thanks to @mithmatt for adding this problem and creating all
 * test cases.
 */
class Solution {
public:
    void BFS(vector<vector<char>>& grid, int i, int j) {
        queue<pair<int,int>> q;
        q.push(make_pair(i,j));
        while (!q.empty()) {
            auto p = q.front();
            q.pop();
            int x = p.first, y = p.second;
            if (grid[x][y] != '1') continue;
            grid[x][y] = '2';
            if (x>0) q.push(make_pair(x-1,y));
            if (x<grid.size()-1) q.push(make_pair(x+1,y));
            if (y>0) q.push(make_pair(x,y-1));
            if (y<grid[x].size()-1) q.push(make_pair(x,y+1));
        }
    }
    int numIslands(vector<vector<char>>& grid) {
        int x = 0;
        for (int i=0, m=grid.size(); i<m; ++i) {
            for (int j=0,n=grid[i].size(); j<n; ++j) {
                if (grid[i][j] == '1') {
                    ++x;
                    BFS(grid, i, j);
                }
            }
        }
        return x;
    }
};
